function FormHandler(n){this.$container=$("."+n.containerClass),this.$triggerButton=this.$container.find("#"+n.triggerButtonId),this.init()}FormHandler.prototype.init=function(){this.bindEvents()},FormHandler.prototype.bindEvents=function(){var n=this;this.$triggerButton.on("click",function(){$(this).is(":checked")?n.hideContainer(this):n.showContainer(this)})},FormHandler.prototype.hideContainer=function(n){$(n).siblings("div").first().slideUp()},FormHandler.prototype.showContainer=function(n){$(n).siblings("div").first().slideDown()},$(document).ready(function(){args={containerClass:"discussion-container",triggerButtonId:"remove-discussion"};new FormHandler(args)});